import { Document, PDFViewer, Page, StyleSheet, Text, View } from '@react-pdf/renderer';
import React, { useState } from 'react';

// Create styles for the PDF
const styles = StyleSheet.create({
    page: {
        flexDirection: 'row',
        backgroundColor: '#E4E4E4'
    },
    section: {
        margin: 10,
        padding: 10,
        flexGrow: 1
    }
});

// Create Document Component
const MyDocument = ({ formData }) => (
    <Document>
        <Page size="A4" style={styles.page}>
            <View style={styles.section}>
                <Text>Name: {formData.name}</Text>
                <Text>Email: {formData.email}</Text>
                <Text>Phone: {formData.phone}</Text>
            </View>
        </Page>
    </Document>
);

const FormAndPdf = () => {
    const [formData, setFormData] = useState({ name: '', email: '', phone: '' });
    const [submit, setSubmit] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({ ...prevState, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setSubmit(true); // Render the PDF viewer when form is submitted
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    placeholder="Enter name"
                />
                <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    placeholder="Enter email"
                />
                <input
                    type="text"
                    name="phone"
                    value={formData.phone}
                    onChange={handleChange}
                    placeholder="Enter phone number"
                />
                <button type="submit">Generate PDF</button>
            </form>
            {submit && (
                <PDFViewer style={{ width: '100%', height: '400px' }}>
                    <MyDocument formData={formData} />
                </PDFViewer>
            )}
        </div>
    );
};

export default FormAndPdf;
