import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { getResumesApi } from '../../../apis/Api'; // Ensure this path matches your project structure
import ApplicantNavbar from "../../../components/ApplicantNavbar";

const PageContainer = styled.div`
  padding: 20px;
  background-color: white;
  min-height: calc(100vh - 60px);  // Assuming the navbar is 60px high
  position: relative;
`;

const ResumesContainer = styled.div`
  max-width: 1600px;
  margin: 0 auto;
  padding: 20px;
  background: white;
  border-radius: 8px;
  // box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.1);
`;

const ResumeCard = styled.div`
  padding: 20px;
  border-bottom: 1px solid #ccc;
  &:last-child {
    border-bottom: none;
  }
`;

const ResumeTitle = styled.h2`
  font-size: 20px;
  color: #65A168;
`;

const ResumeDetails = styled.p`
  font-size: 16px;
  color: #666;
`;

const Title = styled.h1`
  font-size: 32px;
  color: #333;
  text-align: left;
  margin-bottom: 20px;
`;

const FloatingButton = styled.button`
  position: absolute;
  bottom: 20px;
  right: 20px;
  background-color: #108A00;
  color: white;
  padding: 15px;
  border: none;
  border-radius: 50%;
  cursor: pointer;
  font-size: 24px;
  display: flex;
  align-items: center;
  justify-content: center;
  box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.1);
`;

const ResumeList = () => {
    const [resumes, setResumes] = useState([]);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        fetchResumes();
    }, []);

    const fetchResumes = async () => {
        try {
            setLoading(true);
            const res = await getResumesApi(); // Assuming this API call fetches the list of resumes
            setResumes(res.data.resumes);
            setLoading(false);
        } catch (error) {
            console.log(error);
            setLoading(false);
        }
    };

    const handleAddResume = () => {
        navigate('/applicant/create_resume'); // Navigate to resume builder page
    };

    return (
        <>
            <ApplicantNavbar />
            <PageContainer>
                <Title>My Resumes</Title>
                {loading ? (
                    <p>Loading...</p>
                ) : (
                    <ResumesContainer>
                        {resumes.length === 0 ? (
                            <p>No Resumes Have Been Created</p>
                        ) : (
                            resumes.map((resume) => (
                                <ResumeCard key={resume._id}>
                                    <ResumeTitle>{resume.title}</ResumeTitle>
                                    <ResumeDetails>{resume.details}</ResumeDetails>
                                </ResumeCard>
                            ))
                        )}
                    </ResumesContainer>
                )}
                <FloatingButton onClick={handleAddResume}>+</FloatingButton>
            </PageContainer>
        </>
    );
};

export default ResumeList;
