import axios from "axios";

//creating backend Config!
const Api = axios.create({
    baseURL: "http://localhost:5500",
    withCredentials: true,
    headers: {
        "Content-Type": "multipart/form-data"
    }

})

//login api
export const loginUserApi = (data) => Api.post("/api/user/login", data)
//register api 
export const registerUserApi = (data) => Api.post("/api/user/register", data)

//create job API
export const createJobApi = (data) => Api.post("/api/job/create_job", data)

//fetch all open job API
export const getAllOpenJobApi = () => Api.get("/api/job/get_all_open_jobs")

//fetch all open job API
export const updateApplicantProfile = () => Api.put("/api/user/get_all_open_jobs")
