import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background-color: #f0f2f5;
`;

const FormWrapper = styled.div`
  display: flex;
  flex-direction: column;
  background: #fff;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  width: 400px;
  padding: 40px;
  text-align: center;
`;

const Title = styled.h2`
  margin-bottom: 20px;
  font-size: 24px;
  color: #333;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  margin: 10px 0;
  border: 1px solid #ccc;
  border-radius: 5px;
  font-size: 16px;
`;

const Select = styled.select`
  width: 100%;
  padding: 10px;
  margin: 10px 0;
  border: 1px solid #ccc;
  border-radius: 5px;
  font-size: 16px;
  background-color: white;
`;

const Button = styled.button`
  width: 100%;
  padding: 10px;
  border: none;
  border-radius: 5px;
  background-color: #108A00;
  color: white;
  font-size: 16px;
  cursor: pointer;
  margin-top: 20px;
`;

const CreateJobPage = () => {
    const [jobTitle, setJobTitle] = useState('');
    const [workType, setWorkType] = useState('');
    const [description, setDescription] = useState('');
    const [skills, setSkills] = useState('');
    const [qualification, setQualification] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
    }
    // const jobData = {
    //   title: jobTitle,
    //   workType,
    //   description,
    //   skills,
    //   qualification
    // };
    // Example API call to create a job post
    //     const response = await createJobApi(jobData);
    //     if (response.success) {
    //       navigate('/dashboard'); // Redirecting to the dashboard or another appropriate page
    //     } else {
    //       alert('Error creating job post'); // Error handling
    //     }
    //   };

    return (
        <>
            <EmployerNavbar />
            <Container>
                <FormWrapper>
                    <Title>Create Your Job Post</Title>
                    <Input
                        type="text"
                        placeholder="Title"
                        value={jobTitle}
                        onChange={(e) => setJobTitle(e.target.value)}
                    />
                    <Select
                        value={workType}
                        onChange={(e) => setWorkType(e.target.value)}
                    >
                        <option value="">Select Work Type</option>
                        <option value="Remote">Remote</option>
                        <option value="On-site">On-site</option>
                        <option value="Hybrid">Hybrid</option>
                    </Select>
                    <Input
                        type="text"
                        placeholder="Description"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                    />
                    <Input
                        type="text"
                        placeholder="Skills"
                        value={skills}
                        onChange={(e) => setSkills(e.target.value)}
                    />
                    <Input
                        type="text"
                        placeholder="Qualification"
                        value={qualification}
                        onChange={(e) => setQualification(e.target.value)}
                    />
                    <Button onClick={handleSubmit}>Create Job Post</Button>
                </FormWrapper>
            </Container>
        </>
    );
}

export default CreateJobPage
