import axios from "axios";

//creating backend Config!
const Api = axios.create({
    baseURL: "http://localhost:5500",
    withCredentials: true,
    headers: {
        "Content-Type": "multipart/form-data"
    }

})

//creating config
const config = {
    headers: {
        authorization: `Bearer ${localStorage.getItem('token')}`,
    },
};

//API FOR APPLICANT
//login api
export const loginUserApi = (data) => Api.post("/api/user/login", data)
//register api 
export const registerUserApi = (data) => Api.post("/api/user/register", data)

//updateApplicantProfile API
export const updateApplicantProfile = (id, data) => Api.put(`/api/user/update_applicant/${id}`, data, config)

//Resume saving API
export const saveResumeApi = (data) => Api.post("/api/resume/save_resume", data)




//API FOR EMPLOYER
//login api
export const loginEmployerApi = (data) => Api.post("/api/employer/login", data)
//register api 
export const registerEmployerApi = (data) => Api.post("/api/employer/register", data)
//create job API
export const createJobApi = (data) => Api.post("/api/job/create_job", data, config)



//fetch all open job by employer API
export const getAllOpenJobByEmployerApi = (page, limit) => Api.get("/api/job/open_jobs_by_employer", config)

//fetch all open job API
export const getAllOpenJobApi = () => Api.get("/api/job/get_all_open_jobs", config)

//fetch job details by id
export const getJobDetailsByIdApi = () => Api.get("/api/job/get_job_details/:id", config)
