import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import styled from 'styled-components';
import { registerUserApi } from '../../apis/Api';
import { FaUserCircle } from 'react-icons/fa';  // Importing a user icon

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background-color: #fff;
`;

const RegisterFormWrapper = styled.div`
  display: flex;
  background: #fff;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
`;

const ImageWrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #1CB173;
  border-top-left-radius: 10px;
  border-bottom-left-radius: 10px;
  cursor: pointer;
`;

const RegisterForm = styled.div`
  width: 350px;
  padding: 40px;
  text-align: center;
`;

const Image = styled.img`
  width: 300px;
  border-top-left-radius: 10px;
  border-bottom-left-radius: 10px;
`;

const Title = styled.h2`
  margin-bottom: 20px;
  font-size: 24px;
  color: #333;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  margin: 10px 0;
  border: 1px solid #ccc;
  border-radius: 5px;
  font-size: 16px;
`;

const Button = styled.button`
  width: 100%;
  padding: 10px;
  border: none;
  border-radius: 5px;
  background-color: #108A00;
  color: white;
  font-size: 16px;
  cursor: pointer;
  margin-top: 10px;
`;

const Text = styled.p`
  margin-top: 20px;
  font-size: 14px;
  color: #666;
`;

const LoginLink = styled(Link)`
  color: #007bff;
  text-decoration: none;
  &:hover {
    text-decoration: underline;
  }
`;

const RegisterPage = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const navigate = useNavigate();

  const handleFileChange = (event) => {
    setSelectedFile(URL.createObjectURL(event.target.files[0]));
  };

  const handleProfileClick = () => {
    document.getElementById('fileInput').click();
  };

  return (
    <Container>
      <RegisterFormWrapper>
        <ImageWrapper onClick={handleProfileClick}>
          {selectedFile ? <Image src={selectedFile} alt="Profile" /> : <FaUserCircle size={60} color="#fff" />}
          <input type="file" id="fileInput" style={{ display: 'none' }} onChange={handleFileChange} />
        </ImageWrapper>
        <RegisterForm>
          <Title>Register To Job Mate</Title>
          <Input type="text" placeholder="Name" onChange={(e) => setName(e.target.value)} />
          <Input type="email" placeholder="Email Address" onChange={(e) => setEmail(e.target.value)} />
          <Input type="text" placeholder="Phone number" onChange={(e) => setPhone(e.target.value)} />
          <Input type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} />
          <Input type="password" placeholder="Confirm Password" onChange={(e) => setConfirmPassword(e.target.value)} />
          <Button onClick={handleSubmit}>Register</Button>
          <Text>Already Have An Account? <LoginLink to="/login">Login here</LoginLink></Text>
        </RegisterForm>
      </RegisterFormWrapper>
    </Container>
  );
};

export default RegisterPage;
