import React from 'react';
import { FaAward, FaBriefcase, FaFileAlt, FaSearch } from 'react-icons/fa';
import { NavLink } from 'react-router-dom';
import styled from 'styled-components';

const NavbarContainer = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    background-color: #FFFFFF;
    padding: 10px 20px;
    color: #333;
`;

const Logo = styled.img`
    height: 50px;
`;

const NavIcons = styled.div`
    display: flex;
`;

const Icon = styled(NavLink)`
    cursor: pointer;
    font-size: 20px;
    padding: 0 35px;
    color: #ccc; // Grey color for icons
    text-decoration: none;

    &.active {
        color: #108A00; // Green color for active icon
    }
`;

const ApplicantName = styled.div`
    font-size: 20px;
`;

const ProfileImage = styled.img`
    width: 40px; // Adjust based on your design
    height: 40px; // Adjust based on your design
    border-radius: 50%; // Makes the image round
`;

const Divider = styled.hr`
    border: none;
    border-top: 1px solid #DDDDDD;
    margin: 0;
`;

const ApplicantNavbar = () => {

    const applicant = (JSON.parse(localStorage.getItem('user')) || {}).findUser || {};

    return (
        <>
            <NavbarContainer>
                <Logo src="/assets/images/jobmate.png" alt="Logo" />
                <NavIcons>
                    <Icon to="/applicant/dashboard"><FaSearch title="Jobs" /></Icon>
                    <Icon to="/applicant/applied"><FaBriefcase title="Applied" /></Icon>
                    <Icon to="/applicant/resume"><FaFileAlt title="Resume" /></Icon>
                    <Icon to="/applicant/offer"><FaAward title="Offer" /></Icon>
                </NavIcons>
                <ApplicantName>{applicant.name || "name"}</ApplicantName>
                {applicant.userImage && <ProfileImage src={`/path/to/userImage/${applicant.userImage}`} alt="Profile" />}
            </NavbarContainer>
            <Divider />
        </>
    );
};

export default ApplicantNavbar;
