import React, { useState } from 'react';
import styled from 'styled-components';
import { updateApplicationStatusApi } from '../../../apis/Api'; 

const ApplicationCard = ({ application, fetchApplications }) => {
    const [status, setStatus] = useState(application.status);

    const handleStatusChange = (e) => {
        setStatus(e.target.value);
    };

    const handleUpdateStatus = async () => {
        try {
            await updateApplicationStatusApi({
                applicationId: application._id,
                status
            });
            fetchApplications(); // Fetch the updated list of applications
        } catch (error) {
            console.error('Error updating status:', error);
            // Handle error (e.g., show a toast notification)
        }
    };

    return (
        <Card>
            <JobName>{application.job.title}</JobName>
            <ApplicantName>{application.applicant.name}</ApplicantName>
            <ViewResumeButton onClick={() => window.open(`http://localhost:5500${application.resume.fileUrl}`, '_blank')}>
                View Resume
            </ViewResumeButton>
            <StatusDropdown value={status} onChange={handleStatusChange}>
                <option value="shortlisted">Short Listed</option>
                <option value="job-offered">Job Offered</option>
                <option value="hired">Hired</option>
                <option value="complete">Complete</option>
            </StatusDropdown>
            <UpdateButton onClick={handleUpdateStatus}>Update Status</UpdateButton>
        </Card>
    );
};

const Card = styled.div`
  border: 1px solid #ccc;
  padding: 20px;
  margin-bottom: 20px;
  display: flex;
  flex-direction: column;
`;

const JobName = styled.h3`
  margin: 0;
  color: #333;
`;

const ApplicantName = styled.p`
  margin: 5px 0;
  color: #666;
`;

const ViewResumeButton = styled.button`
  background-color: #108A00;
  color: white;
  border: none;
  padding: 10px;
  margin: 10px 0;
  cursor: pointer;
`;

const StatusDropdown = styled.select`
  margin-bottom: 10px;
`;

const UpdateButton = styled.button`
  background-color: #108A00;
  color: white;
  border: none;
  padding: 10px;
  cursor: pointer;
`;

export default ApplicationCard;
