import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { getAllResumesApi } from '../../../apis/Api'; // Ensure this path matches your project structure
import ApplicantNavbar from "../../../components/ApplicantNavbar";
import Loader from '../../../components/Loader';

const PageContainer = styled.div`
  padding: 20px;
  background-color: white;
  min-height: calc(100vh - 60px);  // Assuming the navbar is 60px high
`;

const ResumesContainer = styled.div`
  max-width: 1600px;
  margin: 0 auto;
  padding: 20px;
  background: white;
  border-radius: 8px;
`;

const ResumeCard = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  padding: 20px;
  border-bottom: 1px solid #ccc;
  &:last-child {
    border-bottom: none;
  }
`;

const ResumeTitle = styled.h2`
  font-size: 20px;
  color: #65A168;  // Green color for the resume title
  margin: 0;
`;

const ResumeDetails = styled.p`
  font-size: 16px;
  color: #666;
  margin: 5px 0;
`;

const Button = styled.button`
  background-color: #108A00;
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 40px;  // Rounded corners
  cursor: pointer;
  font-size: 16px;
  margin-top: 10px;
  align-self: flex-start;
`;

const Title = styled.h1`
  font-size: 32px;
  color: #333;
  text-align: left;
  margin-bottom: 26px;
  margin-left: 34px;
`;

const FloatingButton = styled.button`
  position: fixed;
  bottom: 20px;
  right: 20px;
  background-color: #108A00;
  color: white;
  border: none;
  border-radius: 50%;
  width: 60px;
  height: 60px;
  font-size: 30px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const ResumeList = () => {
    const [resumes, setResumes] = useState([]);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        fetchResumes();
    }, []);

    const fetchResumes = async () => {
        try {
            setLoading(true);
            const res = await getAllResumesApi();
            setResumes(res.data.resumes);
            setLoading(false);
        } catch (error) {
            console.error('Error fetching resumes:', error);
            setLoading(false);
        }
    };

    const handleCreateResume = () => {
        navigate('/applicant/create_resume'); // Adjust the path based on your routing setup
    };

    return (
        <>
            <ApplicantNavbar />
            <PageContainer>
                <Title>Your Resumes</Title>
                {loading ? (
                    <Loader />
                ) : resumes.length === 0 ? (
                    <p>No Resumes Have Been Created</p>
                ) : (
                    <ResumesContainer>
                        {resumes.map(resume => (
                            <ResumeCard key={resume._id}>
                                <ResumeTitle>{resume.jobTitle} at {resume.previousCompanyName}</ResumeTitle>
                                <ResumeDetails>{resume.jobDescription}</ResumeDetails>
                                <ResumeDetails>{resume.highestEducation} from {resume.educationInstitute}</ResumeDetails>
                                <Button onClick={() => window.open(`http://localhost:5500${resume.fileUrl}`, '_blank')}>View Resume</Button>
                            </ResumeCard>
                        ))}
                    </ResumesContainer>
                )}
            </PageContainer>
            <FloatingButton onClick={handleCreateResume}>+</FloatingButton>
        </>
    );
};

export default ResumeList;
