import { Document, Page, StyleSheet, Text, pdf } from '@react-pdf/renderer';
import React, { useState } from 'react';
import styled from 'styled-components';
import { saveResumeApi } from '../../../apis/Api';

// Styles for PDF
const styles = StyleSheet.create({
    page: { flexDirection: 'column', padding: 30 },
    section: { marginBottom: 10 },
});

// PDF Document Component
const MyDocument = ({ formData }) => (
    <Document>
        <Page size="A4" style={styles.page}>
            <Text>Name: {formData.name}</Text>
            <Text>Email: {formData.email}</Text>
            <Text>Phone: {formData.phone}</Text>
            <Text>Previous Company: {formData.previousCompanyName}</Text>
            <Text>Job Title: {formData.jobTitle}</Text>
            <Text>Job Duration: {formData.jobDuration}</Text>
            <Text>Job Description: {formData.jobDescription}</Text>
            <Text>Highest Qualification: {formData.highestQualification}</Text>
            <Text>Education Institute: {formData.educationInstitute}</Text>
        </Page>
    </Document>
);

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-top: 20px;
`;

const Input = styled.input`
  margin-bottom: 10px;
  width: 300px;
  padding: 8px;
`;

const Button = styled.button`
  padding: 10px 20px;
  background-color: #108A00;
  color: white;
  border: none;
  border-radius: 5px;
`;

const PdfViewer = styled.iframe`
  width: 100%;
  height: 500px;
  border: none;
  margin-top: 20px;
`;

const FormAndPdf = () => {
    const [formData, setFormData] = useState({
        name: '', email: '', phone: '',
        previousCompanyName: '', jobTitle: '', jobDuration: '',
        jobDescription: '', highestQualification: '', educationInstitute: ''
    });
    const [pdfUrl, setPdfUrl] = useState('');

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleFormSubmit = async () => {
        const doc = <MyDocument formData={formData} />;
        const asPdf = pdf(); // Create an instance of PDF
        asPdf.updateContainer(doc);
        const blob = await asPdf.toBlob(); // Generate blob

        // Set URL for PDF display
        const url = URL.createObjectURL(blob);
        setPdfUrl(url);

        // FormData to send via HTTP
        const fileData = new FormData();
        fileData.append("file", blob, "resume.pdf");

        try {
            const response = await saveResumeApi(fileData);
            console.log('Resume submitted successfully:', response.data);
        } catch (error) {
            console.error('Error submitting resume:', error);
        }
    };

    return (
        <Container>
            <Input type="text" name="name" value={formData.name} onChange={handleInputChange} placeholder="Enter name" />
            {/* Additional inputs omitted for brevity */}
            <Button onClick={handleFormSubmit}>Create Resume</Button>
            {pdfUrl && <PdfViewer src={pdfUrl} />}
        </Container>
    );
};

export default FormAndPdf;
