import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { getUserAppliedJobsApi } from '../../../apis/Api';
import ApplicantNavbar from '../../../components/ApplicantNavbar';
import Loader from '../../../components/Loader';


const PageContainer = styled.div`
  padding: 20px;
  background-color: white;
  min-height: calc(100vh - 60px);  // Assuming the navbar is 60px high
`;

const JobsContainer = styled.div`
  max-width: 1600px;
  margin: 0 auto;
  padding: 20px;
  background: white;
  border-radius: 8px;
`;

const JobCard = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  padding: 20px;
  border-bottom: 1px solid #ccc;
  &:last-child {
    border-bottom: none;
  }
`;

const JobHeader = styled.div`
  display: flex;
  align-items: center;
  width: 100%;
`;

const EmployerImage = styled.img`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  object-fit: cover;
`;

const JobDetails = styled.div`
  margin-left: 20px;
`;

const JobTitle = styled.h2`
  font-size: 20px;
  color: #65A168;  // Green color for the job title
  margin: 0;
`;

const EmployerName = styled.p`
  font-size: 16px;
  color: #333;
  font-weight: bold;
  margin: 0;
`;

const JobDescription = styled.p`
  font-size: 16px;
  color: #666;
  margin-top: 10px;
`;

const Title = styled.h1`
  font-size: 32px;
  color: #333;
  text-align: left;
  margin-bottom: 10px;
  margin-left: 32px;
`;

const AppliedJobsList = () => {
  const [appliedJobs, setAppliedJobs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchAppliedJobs();
  }, []);

  const fetchAppliedJobs = async () => {
    try {
      setLoading(true);
      const res = await getUserAppliedJobsApi();
      setAppliedJobs(res.data.applications);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching applied jobs:', error);
      setLoading(false);
    }
  };

  return (
    <>
      <ApplicantNavbar />
      <PageContainer>
        <Title>Jobs You've Applied For</Title>
        {loading ? (
          <Loader />
        ) : appliedJobs.length === 0 ? (
          <p>No jobs applied for yet.</p>
        ) : (
          <JobsContainer>
            {appliedJobs.map(application => (
              <JobCard key={application._id}>
                <JobHeader>
                  <EmployerImage src={`http://localhost:5500/employerImage/${application.job.employer.employerImage}`} alt="Employer" />
                  <JobDetails>
                    <JobTitle>{application.job.title}</JobTitle>
                    <EmployerName>{application.job.employer.organizationName}</EmployerName>
                  </JobDetails>
                </JobHeader>
                <JobDescription>{application.job.description}</JobDescription>
              </JobCard>
            ))}
          </JobsContainer>
        )}
      </PageContainer>
    </>
  );
};

export default AppliedJobsList;
