import React, { useState } from 'react';
// import { toast } from 'react-toastify'
// import { registerUserApi } from '../../apis/Api'
import { Link } from 'react-router-dom';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background-color: #fff;
`;

const RegisterFormWrapper = styled.div`
  display: flex;
  background: #fff;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
`;

const ImageWrapper = styled.div`
  display: none;
  @media (min-width: 768px) {
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: #1CB173;
    border-top-left-radius: 10px;
    border-bottom-left-radius: 10px;
  }
`;

const RegisterForm = styled.div`
  width: 350px;
  padding: 40px;
  text-align: center;
`;

const Image = styled.img`
  width: 300px;
  border-top-left-radius: 10px;
  border-bottom-left-radius: 10px;
`;

const Title = styled.h2`
  margin-bottom: 20px;
  font-size: 24px;
  color: #333;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  margin: 10px 0;
  border: 1px solid #ccc;
  border-radius: 5px;
  font-size: 16px;
`;

const Button = styled.button`
  width: 100%;
  padding: 10px;
  border: none;
  border-radius: 5px;
  background-color: #108A00;
  color: white;
  font-size: 16px;
  cursor: pointer;
  margin-top: 10px;
`;

const Text = styled.p`
  margin-top: 20px;
  font-size: 14px;
  color: #666;
`;

const LoginLink = styled(Link)`
  color: #007bff;
  text-decoration: none;
  &:hover {
    text-decoration: underline;
  }
`;

const RegisterPage = () => {

  //logic section

  //make a useState for 5 fields
  const [name, setName] = useState('')
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [confirmPassword, setConfirmPassword] = useState('')

  //use state for error message
  const [nameError, setNameError] = useState('')
  const [emailError, setEmailError] = useState('')
  const [passwordError, setPasswordError] = useState('')
  const [confirmPasswordError, setConfirmPasswordError] = useState('')

  //Make a each function for changing the value 
  const handleName = (e) => {
    setName(e.target.value);
  }

  const handleEmail = (e) => {
    setEmail(e.target.value);
  }

  const handlePassword = (e) => {
    setPassword(e.target.value);
  }

  const handleConfirmPassword = (e) => {
    setConfirmPassword(e.target.value);
  }



  //validation
  var validate = () => {
    var isValid = true;

    //validate the firstName
    if (name.trim() === "") {
      setNameError("Name is required")
      isValid = false;
    }

    if (email.trim() === "") {
      setEmailError("Email is required")
      isValid = false;
    }

    if (password.trim() === "") {
      setPasswordError("Password is required")
      isValid = false;
    }

    if (confirmPassword.trim() === "") {
      setConfirmPasswordError("Confirm Password is required")
      isValid = false;
    }

    if (confirmPassword.trim() !== password.trim()) {
      setConfirmPasswordError("Password and confirmation password don't match")
      isValid = false;
    }
    return isValid;


  }

  //submit butoton function
  const handleSubmit = (e) => {
    e.preventDefault()

    //call the validator
    var isValidated = validate();
    if (!isValidated) {
      return
    }

    //register user Api 

    //making json object
    const data = {
      "name": name,
      "email": email,
      "password": password,
    }

    registerUserApi(data).then((res) => {

      //received data: success, message
      if (res.data.success === false) {
        toast.error(res.data.message)
      }
      else {
        toast.success(res.data.message)
      }
      console.log(res.data)
    })


    console.log(name, email, password, confirmPassword)
  }

  return (
    <Container>
      <RegisterFormWrapper>
        <ImageWrapper>
          <Image src="/assets/images/login_image.png" alt="Register" />
        </ImageWrapper>
        <RegisterForm>
          <Title>Register To Job Mate</Title>
          <Input
            type="text"
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <Input
            type="email"
            placeholder="Email Address"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <Input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <Input
            type="password"
            placeholder="Confirm Password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
          <Button onClick={handleRegister}>Register</Button>
          <Text>
            Already Have An Account? <LoginLink to="/login">Login here</LoginLink>
          </Text>
        </RegisterForm>
      </RegisterFormWrapper>
    </Container>
  );
};

export default RegisterPage;
