import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import styled from 'styled-components';
import { getApplicantsForJobApi } from '../../../apis/Api';
import EmployerNavbar from '../../../components/EmployerNavbar';
import Loader from '../../../components/Loader';

const PageContainer = styled.div`
  padding: 20px;
  padding-top: 30px;
  background-color: white;
  min-height: calc(100vh - 60px);  // Assuming the navbar is 60px high
`;

const ApplicantsContainer = styled.div`
  max-width: 1600px;
  margin: 0 auto;
  padding: 20px;
  padding-top: 5px;
  background: white;
  border-radius: 8px;

`;

const ApplicantCard = styled.div`
  padding: 20px;
  border-bottom: 1px solid #ccc;
  &:last-child {
    border-bottom: none;
  }
`;

const ApplicantName = styled.h2`
  font-size: 20px;
  color: #65A168;  // Green color for the applicant name
`;

const ApplicantEmail = styled.p`
  font-size: 16px;
  color: #666;
`;

const Title = styled.h1`
  font-size: 32px;
  color: #333;
  text-align: left;
  margin-bottom: 26px;
  margin-left: 34px;
`;

const JobApplicants = () => {
  const { jobId } = useParams();
  const [applicants, setApplicants] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    getApplicantsForJobApi(jobId).then((res) => {
      setLoading(true);
      setApplicants(res.data.applicants);
      setLoading(false);
    }).catch((error) => {
      console.log(error);
    });
  }, [jobId]);

  return (
    <>
      <EmployerNavbar />
      <PageContainer>
        <Title>Applicants</Title>
        {loading ? (<Loader></Loader>) : (
          <>
            <ApplicantsContainer>
              {applicants.map(applicant => (
                <ApplicantCard key={applicant._id}>
                  <ApplicantName>{applicant.name}</ApplicantName>
                  <ApplicantEmail>{applicant.email}</ApplicantEmail>
                </ApplicantCard>
              ))}
            </ApplicantsContainer>
          </>
        )
        }
      </PageContainer>
    </>
  );
}

export default JobApplicants;
