import React from 'react';
import styled from 'styled-components';

const DialogOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
`;

const DialogContent = styled.div`
  background-color: white;
  padding: 20px;
  border-radius: 8px;
  max-width: 500px;
  width: 100%;
`;

const DialogHeader = styled.h2`
  margin-top: 0;
`;

const ResumeList = styled.ul`
  list-style-type: none;
  padding: 0;
`;

const ResumeItem = styled.li`
  margin-bottom: 10px;
  cursor: pointer;

  &:hover {
    text-decoration: underline;
  }
`;

const Button = styled.button`
  background-color: #108A00;
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 40px;
  cursor: pointer;
  font-size: 16px;
  margin-top: 20px;
  display: block;
  margin-left: auto;
`;

const ResumeSelectionDialog = ({ show, resumes, onClose, onSelectResume }) => {
  if (!show) return null;

  return (
    <DialogOverlay>
      <DialogContent>
        <DialogHeader>Select a Resume</DialogHeader>
        <ResumeList>
          {resumes.map((resume) => (
            <ResumeItem key={resume._id} onClick={() => onSelectResume(resume._id)}>
              {resume.resumeTitle}
            </ResumeItem>
          ))}
        </ResumeList>
        <Button onClick={onClose}>Close</Button>
      </DialogContent>
    </DialogOverlay>
  );
};

export default ResumeSelectionDialog;
