import axios from "axios";

//creating backend Config!
const Api = axios.create({
    baseURL: "http://localhost:5500",
    withCredentials: true,
    headers: {
        "Content-Type": "multipart/form-data"
    }

})

//API FOR APPLICANT
//login api
export const loginUserApi = (data) => Api.post("/api/user/login", data)
//register api 
export const registerUserApi = (data) => Api.post("/api/user/register", data)
//updateApplicantProfile API
export const updateApplicantProfile = (id, data) => Api.put(`/api/user/update_applicant/${id}`, data)


//API FOR EMPLOYER
//login api
export const loginEmployerApi = (data) => Api.post("/api/employer/login", data)
//register api 
export const registerEmployerApi = (data) => Api.post("/api/user/register", data)
//create job API
export const createJobApi = (data) => Api.post("/api/job/create_job", data)




//fetch all open job API
export const getAllOpenJobApi = () => Api.get("/api/job/get_all_open_jobs")
