import React, { useState } from 'react'
import { toast } from 'react-toastify'
import { registerUserApi } from '../../apis/Api'

const Register = () => {

    //logic section

    //make a useState for 5 fields
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [confirmPassword, setConfirmPassword] = useState('')

    //use state for error message
    const [nameError, setNameError] = useState('')
    const [emailError, setEmailError] = useState('')
    const [passwordError, setPasswordError] = useState('')
    const [confirmPasswordError, setConfirmPasswordError] = useState('')

    //Make a each function for changing the value 
    const handleName = (e) => {
        setName(e.target.value);
    }

    const handleEmail = (e) => {
        setEmail(e.target.value);
    }

    const handlePassword = (e) => {
        setPassword(e.target.value);
    }

    const handleConfirmPassword = (e) => {
        setConfirmPassword(e.target.value);
    }



    //validation
    var validate = () => {
        var isValid = true;

        //validate the firstName
        if (name.trim() === "") {
            setNameError("Name is required")
            isValid = false;
        }

        if (email.trim() === "") {
            setEmailError("Email is required")
            isValid = false;
        }

        if (password.trim() === "") {
            setPasswordError("Password is required")
            isValid = false;
        }

        if (confirmPassword.trim() === "") {
            setConfirmPasswordError("Confirm Password is required")
            isValid = false;
        }

        if (confirmPassword.trim() !== password.trim()) {
            setConfirmPasswordError("Password and confirmation password don't match")
            isValid = false;
        }
        return isValid;


    }

    //submit butoton function
    const handleSubmit = (e) => {
        e.preventDefault()

        //call the validator
        var isValidated = validate();
        if (!isValidated) {
            return
        }

        //register user Api 

        //making json object
        const data = {
            "name": name,
            "email": email,
            "password": password,
        }

        registerUserApi(data).then((res) => {

            //received data: success, message
            if (res.data.success == false) {
                toast.error(res.data.message)
            }
            else {
                toast.success(res.data.message)
            }
            console.log(res.data)
        })


        console.log(name, email, password, confirmPassword)
    }

    return (
        <>
            <div className='container'>
                <h1>
                    Create An Account
                </h1>
                <form className='w-50'>
                    <label className='mt-4'>
                        Name: {name}
                    </label>
                    <input onChange={handleName} type='text' className='form-control mt' placeholder='Enter your name'>
                    </input>

                    {
                        nameError && <p className='text-danger'>{nameError}</p>
                    }

                    <label className='mt-4'>
                        Email: {email}
                    </label>
                    <input onChange={handleEmail} type='text' className='form-control' placeholder='Enter your email'>
                    </input>
                    {
                        emailError && <p className='text-danger'>{emailError}</p>
                    }

                    <label className='mt-4'>
                        Password: {password}
                    </label>
                    <input onChange={handlePassword} type='text' className='form-control' placeholder='Enter your password'>
                    </input>
                    {
                        passwordError && <p className='text-danger'>{passwordError}</p>
                    }

                    <label className='mt-4'>
                        Confirm password: {confirmPassword}
                    </label>
                    <input onChange={handleConfirmPassword} type='text' className='form-control mb-4' placeholder='Confirm your password'>
                    </input>
                    {
                        confirmPasswordError && <p className='text-danger'>{confirmPasswordError}</p>
                    }


                    <button onClick={handleSubmit} className='btn btn-dark'> Create an account</button>



                </form>
            </div>
        </>
    )
}

export default Register
