import React, { useState } from 'react';
import styled from 'styled-components';
import { saveResumeApi } from '../../../apis/Api'; // Adjust the path as necessary

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
`;

const Input = styled.input`
  width: 300px;
  padding: 10px;
  margin: 10px;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

const TextArea = styled.textarea`
  width: 300px;
  height: 100px;
  padding: 10px;
  margin: 10px;
  border: 1px solid #ccc;
  border-radius: 4px;
  resize: none;
`;

const Button = styled.button`
  padding: 10px 20px;
  color: white;
  background-color: #108A00; // Your specified green color
  border: none;
  border-radius: 5px;
  cursor: pointer;
  margin-top: 20px;
`;

const ResumeBuilder = () => {
    const [formData, setFormData] = useState({
        previousCompanyName: '',
        jobTitle: '',
        jobDuration: '',
        jobDescription: '',
        highestQualification: '',
        educationInstitute: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({ ...prevState, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        // Implement PDF generation here if necessary or directly send formData to the backend
        // For example:
        try {
            const response = await saveResumeApi(formData);
            console.log('Resume created successfully:', response.data);
        } catch (error) {
            console.error('Error creating resume:', error);
        }
    };

    return (
        <Container>
            <Input
                name="previousCompanyName"
                value={formData.previousCompanyName}
                onChange={handleChange}
                placeholder="Previous Company Name"
            />
            <Input
                name="jobTitle"
                value={formData.jobTitle}
                onChange={handleChange}
                placeholder="Job Title"
            />
            <Input
                name="jobDuration"
                value={formData.jobDuration}
                onChange={handleChange}
                placeholder="Job Duration"
            />
            <TextArea
                name="jobDescription"
                value={formData.jobDescription}
                onChange={handleChange}
                placeholder="Description of Job"
            />
            <Input
                name="highestQualification"
                value={formData.highestQualification}
                onChange={handleChange}
                placeholder="Highest Education Qualification"
            />
            <Input
                name="educationInstitute"
                value={formData.educationInstitute}
                onChange={handleChange}
                placeholder="Education Institute"
            />
            <Button onClick={handleSubmit}>Create Resume</Button>
        </Container>
    );
};

export default ResumeBuilder;
