import React from 'react';
import { FaBriefcase, FaChartLine, FaPencilAlt } from 'react-icons/fa';
import { NavLink } from 'react-router-dom';
import styled from 'styled-components';




const ApplicantName = styled.div`
    font-size: 20px;
    margin-right: 10px;
`;
const ProfileImage = styled.img`
    width: 40px; 
    height: 40px; 
    border-radius: 50%; // Makes the image round
    margin-left: 10px;
`;

const NameAndImageContainer = styled.div`
    display: flex;
    align-items: center;
    gap: 10px;  // This controls the space between the name and image directly
`;
const DropdownMenu = styled.div`
    position: absolute;
    right: 20px;
    top: 60px;
    background-color: #FFF;
    box-shadow: 0px 8px 16px 0px rgba(0,0,0,0.2);
    z-index: 1;
    width: 150px;
    border-radius: 5px;

    & > a {
        display: block;
        padding: 10px;
        color: #333;
        text-decoration: none;

        &:hover {
            background-color: #f2f2f2;
        }
    }
`;


const NavbarContainer = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    background-color: #FFFFFF;
    padding: 10px 20px;
    color: #333;
`;

const Logo = styled.img`
    height: 50px;
`;

const NavIcons = styled.div`
    display: flex;
`;

const Icon = styled(NavLink)`
    cursor: pointer;
    font-size: 20px;
    padding: 0 35px; // Add padding to each icon
    color: #ccc; // Set the default color to grey
    text-decoration: none; // Remove underline from links

    &.active {
        color: #108A00; // Color for the active icon
    }
`;

const CompanyName = styled.div`
    font-size: 20px;
`;

const Divider = styled.hr`
    border: none;
    border-top: 1px solid #DDDDDD;
    margin: 0;
`;
const Navbar = () => {
    return (
        <>
            <NavbarContainer>
                <Logo src="/assets/images/jobmate.png" alt="Logo" />
                <NavIcons>
                    <Icon to="/employer/dashboard" activeClassName="active">
                        <FaBriefcase title="Jobs" />
                    </Icon>
                    <Icon to="/employer/progress" activeClassName="active">
                        <FaChartLine title="Progress" />
                    </Icon>
                    <Icon to="/employer/create_job" activeClassName="active">
                        <FaPencilAlt title="Create Job" />
                    </Icon>
                </NavIcons>
                <CompanyName>Grey</CompanyName>
            </NavbarContainer>
            <Divider />
        </>
    );
};

export default Navbar;
