import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import styled from 'styled-components';
import { getApplicantsForJobApi } from '../../../apis/Api';
import EmployerNavbar from '../../../components/EmployerNavbar';
import Loader from '../../../components/Loader';

const PageContainer = styled.div`
  padding: 20px;
  padding-top: 30px;
  background-color: white;
  min-height: calc(100vh - 60px);  // Assuming the navbar is 60px high
`;

const ApplicantsContainer = styled.div`
  max-width: 1600px;
  margin: 0 auto;
  padding: 20px;
  padding-top: 5px;
  background: white;
  border-radius: 8px;
`;

const ApplicantCard = styled.div`
  display: flex;
  padding: 20px;
  border-bottom: 1px solid #ccc;
  &:last-child {
    border-bottom: none;
  }
`;

const ApplicantImage = styled.img`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  object-fit: cover;
`;

const ApplicantDetails = styled.div`
  margin-left: 20px;
  flex: 1;
`;

const ApplicantName = styled.h2`
  font-size: 20px;
  color: #65A168;  // Green color for the applicant name
  margin: 0;
`;

const ApplicantEmail = styled.p`
  font-size: 16px;
  color: #666;
  margin: 5px 0;
`;

const Button = styled.button`
  background-color: #108A00;
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 40px;  // Rounded corners
  cursor: pointer;
  font-size: 16px;
  margin-top: 10px;
  align-self: flex-start;
`;

const Title = styled.h1`
  font-size: 32px;
  color: #333;
  text-align: left;
  margin-bottom: 26px;
  margin-left: 34px;
`;

const JobApplicants = () => {
  const { jobId } = useParams();
  const [applicants, setApplicants] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    getApplicantsForJobApi(jobId).then((res) => {
      setApplicants(res.data.applicants);
      setLoading(false);
    }).catch((error) => {
      console.log(error);
      setLoading(false);
    });
  }, [jobId]);

  return (
    <>
      <EmployerNavbar />
      <PageContainer>
        <Title>Applicants</Title>
        {loading ? (
          <Loader />
        ) : (
          <ApplicantsContainer>
            {applicants.map(applicant => (
              <ApplicantCard key={applicant._id}>
                <ApplicantImage src={`http://localhost:5500/userImage/${applicant.userImage}`} alt="Applicant" />
                <div>
                  <ApplicantDetails>
                    <ApplicantName>{applicant.name}</ApplicantName>
                    <ApplicantEmail>{applicant.email}</ApplicantEmail>
                  </ApplicantDetails>
                  <Button>Review Resume</Button>
                </div>
              </ApplicantCard>
            ))}
          </ApplicantsContainer>
        )}
      </PageContainer>
    </>
  );
}

export default JobApplicants;
