import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { getJobByIdApi, createApplicationApi } from '../../../apis/Api';
import ApplicantNavbar from '../../../components/ApplicantNavbar';
import { toast } from 'react-toastify';

const PageContainer = styled.div`
  padding: 20px;
  background-color: #f0f2f5;
  min-height: calc(100vh - 60px);  // Assuming the navbar is 60px high
`;

const JobContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 20px;
  background: white;
  border-radius: 8px;
  box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.1);
`;

const JobTitle = styled.h2`
  font-size: 24px;
  color: #65A168;  // Green color for the job title
`;

const EmployerName = styled.h3`
  font-size: 20px;
  color: #333;
`;

const JobDescription = styled.p`
  font-size: 16px;
  color: #666;
  margin-top: 20px;
`;

const JobDetail = styled.p`
  font-size: 16px;
  color: #333;
  margin-top: 10px;
`;

const Button = styled.button`
  background-color: #108A00;
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 40px;  // Rounded corners
  cursor: pointer;
  font-size: 16px;
  margin-top: 20px;
`;

const JobDetailsPage = () => {
  const { id } = useParams();
  const [job, setJob] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    getJobByIdApi(id).then((res) => {
      setJob(res.data.job);
    }).catch((error) => {
      console.log(error);
    });
  }, [id]);

  const handleApply = () => {
    createApplicationApi({ jobId: id }).then((res) => {
      if (res.data.success) {
        toast.success('Application submitted successfully');
        navigate('/applicant/dashboard'); // Redirect to dashboard or any other page
      } else {
        toast.error(res.data.message);
      }
    }).catch((error) => {
      toast.error('Error submitting application');
      console.log(error);
    });
  };

  if (!job) return <p>Loading...</p>;

  return (
    <>
      <ApplicantNavbar />
      <PageContainer>
        <JobContainer>
          <JobTitle>{job.title}</JobTitle>
          <EmployerName>Employer: {job.employer.organizationName}</EmployerName>
          <JobDetail>Work Type: {job.workType}</JobDetail>
          <JobDetail>Skills: {job.skills}</JobDetail>
          <JobDetail>Qualification: {job.qualification}</JobDetail>
          <JobDescription>{job.description}</JobDescription>
          <Button onClick={handleApply}>Apply</Button>
        </JobContainer>
      </PageContainer>
    </>
  );
}

export default JobDetailsPage;
