// ResumeSelectionDialog.jsx

import React, { useState } from 'react';
import styled from 'styled-components';

const DialogOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
`;

const DialogContent = styled.div`
  background-color: white;
  padding: 20px;
  border-radius: 8px;
  max-width: 500px;
  width: 100%;
  position: relative;
`;

const DialogHeader = styled.h2`
  margin-top: 0;
`;

const CloseButton = styled.span`
  position: absolute;
  top: 10px;
  right: 10px;
  font-size: 20px;
  cursor: pointer;
`;

const ResumeList = styled.ul`
  list-style-type: none;
  padding: 0;
`;

const ResumeItem = styled.li`
  display: flex;
  align-items: center;
  margin-bottom: 10px;
`;

const ResumeLabel = styled.label`
  margin-left: 10px;
`;

const Button = styled.button`
  background-color: #108A00;
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 40px;
  cursor: pointer;
  font-size: 16px;
  margin-top: 20px;
  display: block;
  margin-left: auto;
`;

const ResumeSelectionDialog = ({ show, resumes, onClose, onSelectResume }) => {
  const [selectedResume, setSelectedResume] = useState(null);

  const handleApply = () => {
    if (selectedResume) {
      onSelectResume(selectedResume);
    } else {
      alert("Please select a resume before applying.");
    }
  };

  if (!show) return null;

  return (
    <DialogOverlay>
      <DialogContent>
        <CloseButton onClick={onClose}>Ã—</CloseButton>
        <DialogHeader>Select a Resume</DialogHeader>
        <ResumeList>
          {resumes.map((resume) => (
            <ResumeItem key={resume._id}>
              <input
                type="radio"
                name="selectedResume"
                value={resume._id}
                onChange={() => setSelectedResume(resume._id)}
              />
              <ResumeLabel>{resume.resumeTitle}</ResumeLabel>
            </ResumeItem>
          ))}
        </ResumeList>
        <Button onClick={handleApply}>Apply</Button>
      </DialogContent>
    </DialogOverlay>
  );
};

export default ResumeSelectionDialog;
