import React from 'react';
import styled from 'styled-components';

// Styled components
const PageContainer = styled.div`
  padding: 20px;
  background-color: #f0f2f5;
  min-height: calc(100vh - 60px);  // Assuming the navbar is 60px high
`;

const JobsContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
  background: white;
  border-radius: 8px;
  box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.1);
`;

const JobCard = styled.div`
  padding: 20px;
  border-bottom: 1px solid #ccc;
  &:last-child {
    border-bottom: none;
  }
`;

const JobTitle = styled.h2`
  font-size: 20px;
  color: #333;
`;

const JobDescription = styled.p`
  font-size: 16px;
  color: #666;
`;

const Button = styled.button`
  background-color: #108A00;
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  font-size: 16px;
`;

// Mock data for the jobs
const jobs = [
  {
    id: 1,
    title: 'Full Stack MERN Developer',
    description: 'Responsible for building scalable, responsive, and high-performance web applications.',
  },
  {
    id: 2,
    title: 'Full Stack MERN Developer',
    description: 'Responsible for building scalable, responsive, and high-performance web applications.',
  },
  // Add more jobs as needed
];

const EmployerDashboard = () => {
  return (
    <PageContainer>
      <JobsContainer>
        {jobs.map(job => (
          <JobCard key={job.id}>
            <JobTitle>{job.title}</JobTitle>
            <JobDescription>{job.description}</JobDescription>
            <Button>Review Applicants</Button>
          </JobCard>
        ))}
      </JobsContainer>
    </PageContainer>
  );
}

export default EmployerDashboard;
