import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { getAllOpenJobApi } from "../../apis/Api";
import EmployerNavbar from "../../components/EmployerNavbar";

const PageContainer = styled.div`
  padding: 20px;
  background-color: #f0f2f5;
  min-height: calc(100vh - 60px);  // Assuming the navbar is 60px high
`;

const JobsContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
  background: white;
  border-radius: 8px;
  box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.1);
`;

const JobCard = styled.div`
  padding: 20px;
  border-bottom: 1px solid #ccc;
  &:last-child {
    border-bottom: none;
  }
`;

const JobTitle = styled.h2`
  font-size: 20px;
  color: #65A168;  // Green color for the job title
`;

const JobDescription = styled.p`
  font-size: 16px;
  color: #666;
`;

const Button = styled.button`
  background-color: #108A00;
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 40px;  // Rounded corners
  cursor: pointer;
  font-size: 16px;
`;

const Title = styled.h1`
  font-size: 24px;
  color: #333;
  text-align: center;
  margin-bottom: 20px;
`;

// Pagination styles
const PaginationContainer = styled.div`
  display: flex;
  justify-content: center;
  margin-top: 20px;
`;

const PageNumber = styled.button`
  background: none;
  border: none;
  padding: 5px 10px;
  cursor: pointer;
  color: #333;
  font-size: 16px;

  &:hover {
    text-decoration: underline;
  }

  &.active {
    font-weight: bold;
    text-decoration: underline;
  }
`;
const EmployerDashboard = () => {
    //state for all fetched jobs

    const [jobs, setJobs] = useState([])

    //call api initally on page load
    useEffect(() => {
        getAllOpenJobApi().then((res) => {
            setJobs(res.data.jobs)
        }).catch((error) => {
            console.log(error)
        })
    }, [])
    console.log(jobs)


    return (
        <>
            <EmployerNavbar />

            <PageContainer>
                <JobsContainer>
                    {jobs.map(job => (
                        <JobCard key={job.id}>
                            <JobTitle>{job.title}</JobTitle>
                            <JobDescription>{job.description}</JobDescription>
                            <Button>Review Applicants</Button>
                        </JobCard>
                    ))}
                </JobsContainer>
            </PageContainer>

        </>

    );
}

export default EmployerDashboard;
