import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { getAllOpenJobApi } from "../../../apis/Api";
import ApplicantNavbar from "../../../components/ApplicantNavbar";
import Loader from '../../../components/Loader';

const PageContainer = styled.div`
  padding: 20px;
  background-color: #f0f2f5;
  min-height: calc(100vh - 60px);  // Assuming the navbar is 60px high
`;

const JobsContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
  background: white;
  border-radius: 8px;
  box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.1);
`;

const JobCard = styled.div`
  display: flex;
  align-items: flex-start;
  padding: 20px;
  border-bottom: 1px solid #ccc;
  &:last-child {
    border-bottom: none;
  }
`;

const EmployerImage = styled.img`
  width: 80px;
  height: 80px;
  border-radius: 50%;
  object-fit: cover;
`;

const JobDetails = styled.div`
  margin-left: 20px;
  flex: 1;
`;

const JobTitle = styled.h2`
  font-size: 20px;
  color: #65A168;  // Green color for the job title
  margin: 0;
`;

const EmployerName = styled.p`
  font-size: 16px;
  color: #333;
  margin: 0;
`;

const JobDescription = styled.p`
  font-size: 16px;
  color: #666;
  margin-top: 10px;
`;

const Button = styled.button`
  background-color: #108A00;
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 40px;  // Rounded corners
  cursor: pointer;
  font-size: 16px;
  margin-top: 10px;
`;

const Title = styled.h1`
  font-size: 24px;
  color: #333;
  text-align: center;
  margin-bottom: 26px;
`;

// Pagination styles
const PaginationContainer = styled.div`
  display: flex;
  justify-content: center;
  margin-top: 20px;
`;

const PageNumber = styled.button`
  background: none;
  border: none;
  padding: 5px 10px;
  cursor: pointer;
  color: #333;
  font-size: 16px;

  &:hover {
    text-decoration: underline;
  }

  &.active {
    font-weight: bold;
    text-decoration: underline;
  }
`;

const ApplicantDashboard = () => {
  const [jobs, setJobs] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [jobsPerPage] = useState(4); // Can be adjusted based on requirements
  const [totalJobs, setTotalJobs] = useState(0);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    fetchJobs(currentPage);
  }, [currentPage]);

  const fetchJobs = async (page) => {
    try {
      setLoading(true);
      const res = await getAllOpenJobApi(page, jobsPerPage);
      setJobs(res.data.jobs);
      setTotalJobs(res.data.totalJobs);
      setLoading(false);
    } catch (error) {
      console.log(error);
      setLoading(false);
    }
  };

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <>
      <ApplicantNavbar />
      <PageContainer>
        <Title>Jobs</Title>
        {loading ? <Loader /> : (
          <>
            <JobsContainer>
              {jobs.map(job => (
                <JobCard key={job._id}>
                  <EmployerImage src={`http://localhost:5500/employerImage/${job.employer.employerImage}`} alt="Employer" />
                  <JobDetails>
                    <JobTitle>{job.title}</JobTitle>
                    <EmployerName>{job.employer.organizationName}</EmployerName>

                  </JobDetails>

                </JobCard>
              ))}
            </JobsContainer>
            <PaginationContainer>
              {[...Array(Math.ceil(totalJobs / jobsPerPage)).keys()].map(number => (
                <PageNumber
                  key={number + 1}
                  onClick={() => paginate(number + 1)}
                  className={currentPage === number + 1 ? 'active' : ''}
                >
                  {number + 1}
                </PageNumber>
              ))}
            </PaginationContainer>
          </>
        )}
      </PageContainer>
    </>
  );
}

export default ApplicantDashboard;
